{"title":"Average time per node","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": "Average Execution Time per Node (ms)",
  "data": {
    "url": {
      "index": "otel-v1-apm-span-*",
      "body": {
        "size": 0,
        "query": {
          "bool": {
            "must": [
              { "match_phrase": { "name": "FLOW_NODE_EXECUTION_COMPLETED" } }
            ]
          }
        },
        "aggs": {
          "traces": {
            "terms": { "field": "traceId", "size": 100 },
            "aggs": {
              "spans_sequence": {
                "top_hits": {
                  "_source": {
                    "includes": ["events", "name"]
                  },
                  "size": 50
                }
              }
            }
          }
        }
      }
    },
    "format": {
      "property": "aggregations.traces.buckets"
    }
  },
  "transform": [
    {
      "comment": "1. Flatten the hits from the traces",
      "flatten": ["spans_sequence.hits.hits"],
      "as": ["hit"]
    },
    {
      "comment": "2. Flatten the events array inside each span",
      "flatten": ["hit._source.events"],
      "as": ["event_entry"]
    },
    {
      "comment": "3. Filter for Node Completion events only",
      "filter": "datum.event_entry.name == 'FLOW_NODE_EXECUTION_COMPLETED'"
    },
    {
      "comment": "4. Extract Node ID",
      "calculate": "datum.event_entry.attributes.node_id",
      "as": "Node ID"
    },
    {
      "comment": "5. Extract Duration (convert string to number)",
      "calculate": "toNumber(datum.event_entry.attributes.duration_ms)",
      "as": "Duration_MS"
    }
  ],
  "mark": "bar",
  "encoding": {
    "y": {
      "field": "Node ID",
      "type": "nominal",
      "sort": "-x",
      "title": "Flow Node",
      "axis": {"labelFontSize": 12, "titleFontSize": 14}
    },
    "x": {
      "field": "Duration_MS",
      "type": "quantitative",
      "aggregate": "mean",
      "title": "Average Duration (ms)"
    },
    "color": {
      "field": "Node ID",
      "type": "nominal",
      "legend": null,
      "scale": {"scheme": "tableau10"}
    },
    "tooltip": [
      {"field": "Node ID", "title": "Node"},
      {"field": "Duration_MS", "aggregate": "mean", "format": ".2f", "title": "Avg Time (ms)"},
      {"field": "Duration_MS", "aggregate": "min", "title": "Min Time (ms)"},
      {"field": "Duration_MS", "aggregate": "max", "title": "Max Time (ms)"},
      {"field": "Duration_MS", "aggregate": "count", "title": "Total Executions"}
    ]
  },
  "layer": [
    {
      "mark": "bar"
    },
    {
      "mark": {
        "type": "text",
        "align": "left",
        "baseline": "middle",
        "dx": 3
      },
      "encoding": {
        "text": {
          "field": "Duration_MS",
          "aggregate": "mean",
          "format": ".1f"
        }
      }
    }
  ]
}"}}

