{"title":"Authentication Flow","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "padding": {"top": 40, "left": 20, "right": 20, "bottom": 20},
  "title": {
    "text": "Authentication Pipeline Flow",
    "fontSize": 18,
    "anchor": "middle",
    "color": "#2c3e50",
    "offset": 40
  },
  "config": {
    "view": {"stroke": "transparent"},
    "axis": {
      "grid": false,
      "domain": false,
      "ticks": false,
      "labelFontSize": 12,
      "titleFontSize": 14
    }
  },
  "data": {
    "url": {
      "index": "otel-v1-apm-span*",
      "body": {
        "size": 0,
        "query": {
          "bool": {
            "must": [
              {"term": {"span.attributes.component": "FlowEngine"}},
              {"term": {"name": "FLOW_NODE_EXECUTION_COMPLETED"}},
              {"term": {"span.attributes.flow_type": "AUTHENTICATION"}}
            ]
          }
        },
        "aggs": {
          "by_trace": {
            "terms": {"field": "traceId", "size": 3000},
            "aggs": {
              "path": {
                "top_hits": {
                  "size": 10,
                  "sort": [{"span.attributes.step_number": "asc"}],
                  "_source": ["span.attributes.node_id", "span.attributes.step_number"]
                }
              }
            }
          }
        }
      }
    },
    "format": {"property": "aggregations.by_trace.buckets"}
  },
  "transform": [
    {
      "flatten": ["path.hits.hits"],
      "as": ["span_doc"]
    },
    {"calculate": "datum.span_doc._source['span.attributes.node_id']", "as": "node_id"},
    {"calculate": "toNumber(datum.span_doc._source['span.attributes.step_number'])", "as": "step_num"},
    {
      "comment": "1. Count visits per step",
      "joinaggregate": [{"op": "count", "as": "node_visit_count"}],
      "groupby": ["step_num"]
    },
    {
      "comment": "2. Get total hits for percentage calculation",
      "joinaggregate": [{"op": "count", "as": "total_hits"}]
    },
    {
      "calculate": "format(datum.node_visit_count / datum.total_hits, '.0%')",
      "as": "percentage"
    },
    {
      "window": [{"op": "rank", "as": "rank_in_step"}],
      "groupby": ["step_num"]
    },
    {
      "filter": "datum.rank_in_step == 1"
    },
    {
      "window": [
        {"op": "lead", "field": "step_num", "as": "next_step"},
        {"op": "lead", "field": "node_visit_count", "as": "next_node_count"}
      ],
      "sort": [{"field": "step_num"}]
    }
  ],
  "layer": [
    {
      "comment": "1. Backbone Line - Thickness tied to count",
      "transform": [{"filter": "isValid(datum.next_step)"}],
      "mark": {"type": "rule", "color": "#bdc3c7", "strokeCap": "round"},
      "encoding": {
        "x": {"field": "step_num", "type": "ordinal"},
        "x2": {"field": "next_step"},
        "y": {"value": 0},
        "strokeWidth": {
          "field": "node_visit_count",
          "type": "quantitative",
          "scale": {"range": [2, 12]},
          "legend": null
        }
      }
    },
    {
      "comment": "2. Step Nodes",
      "mark": {"type": "circle", "opacity": 1, "stroke": "white", "strokeWidth": 3},
      "encoding": {
        "x": {"field": "step_num", "type": "ordinal", "axis": {"title": "Authentication Sequence", "offset": 20}},
        "y": {"value": 0},
        "size": {
          "field": "node_visit_count",
          "type": "quantitative",
          "scale": {"range": [600, 3000]},
          "legend": null
        },
        "color": {
          "field": "node_id",
          "type": "nominal",
          "scale": {"scheme": "set2"},
          "legend": null
        },
        "tooltip": [
          {"field": "node_id", "title": "Step Name"},
          {"field": "node_visit_count", "title": "Total Hits"},
          {"field": "percentage", "title": "Flow %"}
        ]
      }
    },
    {
      "comment": "3. Step Labels (Node IDs)",
      "mark": {"type": "text", "dy": -45, "fontSize": 13, "fontWeight": 600},
      "encoding": {
        "x": {"field": "step_num", "type": "ordinal"},
        "y": {"value": 0},
        "text": {"field": "node_id"},
        "color": {"value": "#2c3e50"}
      }
    },
    {
      "comment": "4. Count/Percentage Labels INSIDE or NEAR Node",
      "mark": {"type": "text", "baseline": "middle", "fontSize": 10, "fontWeight": "bold"},
      "encoding": {
        "x": {"field": "step_num", "type": "ordinal"},
        "y": {"value": 0},
        "text": {"field": "node_visit_count"},
        "color": {"value": "white"}
      }
    }
  ]
}"}}

